import React, { useState, useEffect } from 'react';

const initData = [
    {id: 1 ,title: 'เงินเดือน' , amount: 20000},
    {id: 2 ,title: 'ค่ารถ' , amount: -2000},
    {id: 3 ,title: 'ค่าอาหาร' , amount: -9800},
    {id: 4 ,title: 'ค่าผ่อนบ้าน' , amount: -9800},
    {id: 5 ,title: 'ค่าน้ำ-ค่าไฟ' , amount: -1500},
  ]

const App = () => {
  const [products, setProducts] = useState(initData);
useEffect(() => {
  fetchProducts();
}, []);
const fetchProducts = () => {
  fetch('https://jsonplaceholder.typicode.com/todos/1')
    .then((res) => res.json())
    .catch((err) => {
      console.log(err);
    }).then((json) => setProducts(json))
};
console.log(products);

return (
    <div>
      <h1>Featured Products</h1>
      <div className='item-container'>
        {products.map((product) => (
          <div className='card'>
             {product.title}
          </div>
        ))}
      </div>
    </div>
  );
};
export default App;
