import { useState } from 'react'
import './FormComponents.css'
import { v4 as uuidv4 } from 'uuid';

const FormComponents = props =>{

    // const inputTiltle = (e) =>{
    //     setTitle(e.target.value)
    // }
    // const inputAmount = (e) =>{
    //     setAmount(e.target.value)
    // }
    
    const [title,setTitle] = useState("")
    const [amount,setAmount] = useState("")
    const [error,setError] = useState({
        name: "",
        mount:"",
    });

    const handleValidation = () => {
        let formIsValid = true;
        if(!title){
            formIsValid = false;
            setError({name: "กรุณากรอกรายการ"})
            return false
        }if(!amount){
            formIsValid = false;
            setError({mount: "กรุณากรอกจำนวนเงิน"})
            return false
        }      
        return formIsValid
    }

    const saveItem = (e) =>{       
        if(handleValidation()){   
            e.preventDefault()    
            const itemData = {
                id:uuidv4(),
                title:title,
                amount:Number(amount)
            }
            props.onAdditem(itemData)
            setTitle('')
            setAmount('')
            setError('')
        }else{                      
            e.preventDefault()       
        }
    }

    return(
        <div>
            <form onSubmit={saveItem}>
                <div className="form-control">
                    <label>ชื่อรายการ</label><span style={{ color: "red" }}>{error.name}</span>
                    <input type="text" placeholder="ระบุรายการ" onChange={(e) => setTitle(e.target.value)} value={title}></input>
                </div>
                <div className="form-control">
                    <label>จำนวนเงิน</label><span style={{ color: "red" }}>{error.mount}</span>
                    <input type="number" placeholder="จำนวนเงิน" onChange={(e) => setAmount(e.target.value)} value={amount}></input>
                </div>
                <div>
                    <button type="submit" className="btn">เพิ่มข้อมูล</button>
                </div>
            </form>
        </div>
    );
}

export default FormComponents
